INSERT INTO `destilado_agave` VALUES (1,'<p>destilado agave</p>\r\n<p>prueba cesar...</p>',1,'2023-06-21 05:28:33','2023-06-21 05:32:25',NULL),(2,'<p><strong>Destilado agave 2023</strong></p>',1,'2023-06-22 04:02:14','2023-06-22 04:02:37',NULL),(3,'<p><span style=\"font-size: 14pt;\">En el marco del 90 aniversario de la Universidad Aut&oacute;noma de Nuevo Le&oacute;n, se lanza el &ldquo;Mezcal Flammam&rdquo;. Un mezcal completamente artesanal, elaborado a base de una diversidad de agaves nativos, completamente org&aacute;nicos que crecen de manera silvestre en el Cerro de Picachos, en Iturbide, Nuevo Le&oacute;n, M&eacute;xico.</span></p>\r\n<p><span style=\"font-size: 14pt;\">Bautizado como <strong>&ldquo;MEZCAL FLAMMAM&rdquo; </strong>en honor al lema de nuestra instituci&oacute;n, <strong>&ldquo;Alere Flammam Veritatis&rdquo;</strong>, que en espa&ntilde;ol significa; <strong>&ldquo;Alentando la Flama de la Verdad&rdquo;.</strong></span></p>\r\n<p><span style=\"font-size: 14pt;\">Esta iniciativa tiene como objetivo potenciar el desarrollo del sur del estado y surge como un proyecto de investigaci&oacute;n multidisciplinario social y productivo, en conjunto con investigadores de la Universidad Aut&oacute;noma de Nuevo Le&oacute;n.</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 24pt; color: #2dc26b;\">Ver m&aacute;s&nbsp;</span></p>',1,'2023-06-23 05:30:17','2023-06-23 05:30:36',NULL);
