INSERT INTO `museo` VALUES (1,'Museo Universitario de  Ciencias','<p><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <iframe style=\"width: 574px; height: 322px;\" src=\"https://www.youtube.com/embed/0vVQb7R7jRs\" width=\"574\" height=\"322\" allowfullscreen=\"allowfullscreen\"></iframe></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Conoce el nuevo Museo Universitario de Ciencias de la UANL, un recinto de conocimiento cient&iacute;fico y actividades interactivas dedicado a los ni&ntilde;os y j&oacute;venes de Nuevo Le&oacute;n.</span></p>\r\n<p class=\"MsoNormal\" style=\"line-height: 1;\">&nbsp;</p>\r\n<p style=\"text-align: center;\"><strong>EXPOSICIONES</strong></p>\r\n<p style=\"text-align: left;\"><img src=\"http://127.0.0.1:8000/storage/images/1V7QXUzjg7OtARTz3Dpf28KLBKlvVOYY2kv21tZZ.png\" alt=\"\" />&nbsp;</p>\r\n<p style=\"text-align: left;\"><strong style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt; text-align: center;\"><span lang=\"ES-TRAD\">MI-YO</span></strong></p>\r\n<p style=\"text-align: left;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><strong><span lang=\"ES-TRAD\">Lo que te hace &uacute;nico</span></strong></span></p>\r\n<p class=\"MsoNormal\" style=\"line-height: 1; text-align: left;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><em><span lang=\"ES-TRAD\" style=\"color: #2e74b5;\">Ejercita tu cuerpo</span></em></span></p>\r\n<p class=\"MsoNormal\" style=\"line-height: 1; text-align: left;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><em><span lang=\"ES-TRAD\" style=\"color: #2e74b5;\">y tambi&eacute;n tu mente</span></em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><span lang=\"ES-TRAD\">Esta exposici&oacute;n pone a prueba tu conocimiento sobre el cuerpo humano sin dejar de lado la diversi&oacute;n. A trav&eacute;s de actividades que van desde identificar figuras geom&eacute;tricas hasta jugar baloncesto, conocer&aacute;s sobre los elementos que componen nuestro organismo y su funcionamiento.</span></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><span lang=\"ES-TRAD\"><img style=\"font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, Oxygen, Ubuntu, Cantarell, \'Open Sans\', \'Helvetica Neue\', sans-serif; font-size: medium;\" title=\"Alimentacion.jpg\" src=\"http://127.0.0.1:8000/storage/images/mSnz9dsTozABpDZG82qYFJl5QfOoQpRRDPDvOwFr.jpg\" alt=\"\" width=\"305\" height=\"227\" /></span></span></p>\r\n<p class=\"MsoNormal\" style=\"line-height: 1; text-align: left;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><strong><span lang=\"ES-TRAD\">Come Bien,</span></strong></span></p>\r\n<p class=\"MsoNormal\" style=\"line-height: 1; text-align: left;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><strong><span lang=\"ES-TRAD\">Juega Bien</span></strong></span></p>\r\n<p class=\"MsoNormal\"><span lang=\"ES-TRAD\" style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif;\">Adem&aacute;s de cultivar nuestra mente, es necesario comer sanamente, por lo que presentamos &ldquo;Come bien y juega bien&rdquo;, una secci&oacute;n del museo que promueve un estilo de vida saludable mediante la correcta selecci&oacute;n de alimentos.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><span lang=\"ES-TRAD\">Aqu&iacute; aprender&aacute;s a mejorar tus h&aacute;bitos y obtener m&aacute;s energ&iacute;a con una dieta balanceada y nutritiva.</span></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><span lang=\"ES-TRAD\"><img style=\"font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, Oxygen, Ubuntu, Cantarell, \'Open Sans\', \'Helvetica Neue\', sans-serif; font-size: medium;\" title=\"Fisica.jpg\" src=\"http://127.0.0.1:8000/storage/images/ImJtV9Byhw6nknmufNSCgmdjzDLnFANXP1BfGHG3.jpg\" alt=\"\" width=\"305\" height=\"227\" /></span></span></p>\r\n<p class=\"MsoNormal\" style=\"line-height: 1; text-align: left;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><strong><span lang=\"ES-TRAD\">F&iacute;sica y</span></strong></span></p>\r\n<p class=\"MsoNormal\" style=\"line-height: 1; text-align: left;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><strong><span lang=\"ES-TRAD\">Astronom&iacute;a</span></strong></span></p>\r\n<p class=\"MsoNormal\" style=\"line-height: 1;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><em><span lang=\"ES-TRAD\" style=\"color: #2e74b5;\">Conoce los</span></em></span></p>\r\n<p class=\"MsoNormal\" style=\"line-height: 1;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><em><span lang=\"ES-TRAD\" style=\"color: #2e74b5;\">principios de la f&iacute;sica</span></em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><span lang=\"ES-TRAD\">El objetivo fundamental de &ldquo;F&iacute;sica recreativa&rdquo; es estimular la curiosidad y creatividad cient&iacute;fica de nuestros visitantes preservando el car&aacute;cter l&uacute;dico e interactivo de nuestro museo. Aprende sobre f&iacute;sica, mec&aacute;nica cl&aacute;sica, astronom&iacute;a y &oacute;ptica con estos divertidos experimentos.</span></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><span lang=\"ES-TRAD\"><img style=\"font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, Oxygen, Ubuntu, Cantarell, \'Open Sans\', \'Helvetica Neue\', sans-serif; font-size: medium;\" title=\"Jardin1.jpg\" src=\"http://127.0.0.1:8000/storage/images/fcY8wjZUmMbe3WNDyD0tSVzXbviWxjhUhkVMmDiD.jpg\" alt=\"\" width=\"305\" height=\"227\" /></span></span></p>\r\n<p class=\"MsoNormal\" style=\"line-height: 1; text-align: left;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><strong><span lang=\"ES-TRAD\">Jard&iacute;n</span></strong></span></p>\r\n<p class=\"MsoNormal\" style=\"line-height: 1; text-align: left;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><strong><span lang=\"ES-TRAD\">de la Ciencia</span></strong></span></p>\r\n<p class=\"MsoNormal\"><span lang=\"ES-TRAD\" style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif;\">Nuestro viaje termina en la parte exterior, un espacio al aire libre donde tambi&eacute;n se abordan algunas ramas de la f&iacute;sica y matem&aacute;ticas, adem&aacute;s se ponen a prueba el sentido auditivo y visual.</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"ES-TRAD\" style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif;\">En estas instalaciones los visitantes podr&aacute;n entender a&uacute;n mejor la ciencia interactuando y jugando libremente con las exhibiciones.</span></p>\r\n<p class=\"MsoNormal\" style=\"line-height: 1;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><em><span lang=\"ES-TRAD\" style=\"color: #2e74b5;\">Jugar es la mejor</span></em></span></p>\r\n<p class=\"MsoNormal\" style=\"line-height: 1;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"><em><span lang=\"ES-TRAD\" style=\"color: #2e74b5;\">manera de aprender</span></em></span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 14pt;\"><strong style=\"mso-bidi-font-weight: normal;\"><span lang=\"ES-TRAD\">Contacto</span></strong></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"> </span></p>\r\n<p class=\"MsoNormal\" style=\"line-height: 1.1; text-align: center;\"><span lang=\"ES-TRAD\" style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif;\">Tel&eacute;fono: 8183294000 ext. 3369</span></p>\r\n<p style=\"line-height: 1.1; text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"> </span></p>\r\n<p class=\"MsoNormal\" style=\"line-height: 1.1; text-align: center;\"><span lang=\"ES-TRAD\" style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif;\">Correo: museouniversitario@uanl.edu.mx</span></p>\r\n<p style=\"line-height: 1.1; text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"> </span></p>\r\n<p class=\"MsoNormal\" style=\"line-height: 1.1; text-align: center;\"><span lang=\"ES-TRAD\" style=\"font-size: 12pt; font-family: arial, helvetica, sans-serif;\">Vicente Su&aacute;rez 1060 Norte,</span></p>\r\n<p style=\"line-height: 1.1; text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\"> </span></p>\r\n<p class=\"MsoNormal\" style=\"line-height: 1.1; text-align: center;\"><span lang=\"ES-TRAD\" style=\"font-size: 10.0pt; font-family: \'Arial\',sans-serif; mso-bidi-font-family: \'Times New Roman\'; mso-bidi-theme-font: minor-bidi;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: 12pt;\">Colonia Modelo, Monterrey, N.L. CP. 64580</span></span></p>\r\n<p>&nbsp;</p>','museo/Rw1cnOcq4vjBsmtk7UsmfFlHgBiMXIkLX5sjKlDI.jpg','2023-01-18','2023-04-19','museo-universitario-de-ciencias',1,'2023-01-18 23:30:06','2023-04-25 03:58:39',NULL,'documentos/7MnOcklflfU7cNOlx3j1AaWC7FcuAuRD7xJ0KHSF.pdf'),(2,'asdf','<p>sdfafsa</p>','museo/INP3J5rTt2TqCCFTWiYkgcNclWv8xeqTOxX73krl.png','2023-09-26','2023-09-30','asdf',1,'2023-09-27 04:48:03','2023-09-27 04:48:08',NULL,'documentos/KgMMv0xUUrAWoAjX29szkMQR2m8ELcONIbbfTBCK.pdf');
