INSERT INTO `observatorio` VALUES (1,'Titulo prueba','<p>Prueba........................</p>',1,'2023-05-16 21:46:48','2023-05-16 23:10:13',NULL),(2,'Vive la experiencia','<h4 class=\"x-el x-el-h4 c1-1 c1-2 c1-1m c1-1a c1-94 c1-14 c1-12 c1-11 c1-13 c1-1e c1-95 c1-56 c1-96 c1-57 c1-58 c1-59 c1-5a\" style=\"box-sizing: inherit; margin: 0px 0px 24px; line-height: 1.25; font-size: 36px; color: #ee661a; overflow-wrap: break-word; font-family: Montserrat, sans-serif; max-width: 100%; background-color: #ffffff; text-align: center;\" role=\"heading\" aria-level=\"4\" data-ux=\"ContentHeading\" data-aid=\"ABOUT_HEADLINE_RENDERED0\" data-typography=\"HeadingDelta\">Observatorio Astron&oacute;mico Universitario</h4>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #757783; font-family: \'Kumbh Sans\', sans-serif; font-size: 18px; background-color: #ffffff;\"><span style=\"box-sizing: border-box; color: #5e5e5e; font-family: Lusitana, Georgia, serif; text-align: center;\">Te invitamos a ser parte de esta formidable excursi&oacute;n al cerro el Picacho en el Municipio de Iturbide Nuevo Leon, a 2400 msnm al Observatorio Astron&oacute;mico Universitario TLAPIANI, esta es una actividad astron&oacute;mica guiada por personal profesional y astr&oacute;nomos de la UANL. Tendr&aacute;s la oportunidad de platicar con los astr&oacute;nomos, aprender mas de su importante trabajo, conocer el equipo que se utiliza para esta tan importante actividad cient&iacute;fica, y ser parte de esta nueva actividad en el Estado de Nuevo Leon que es el Turismo Cient&iacute;fico.</span></p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #757783; font-family: \'Kumbh Sans\', sans-serif; font-size: 18px; background-color: #ffffff;\">&nbsp;</p>\r\n<h4 class=\"x-el x-el-h4 c1-1 c1-2 c1-1m c1-1a c1-94 c1-14 c1-12 c1-11 c1-13 c1-1e c1-95 c1-56 c1-96 c1-57 c1-58 c1-59 c1-5a\" style=\"box-sizing: inherit; margin: 0px 0px 24px; line-height: 1.25;