INSERT INTO `practicas_preguntas_frecuentes` VALUES (1,'¿Que son las prácticas profesionales?','2022-09-13 14:29:51','<p><span style=\"color: #202124; font-family: Arial, sans-serif; font-size: 18px; background-color: #ffffff;\">Se entiende por&nbsp;</span><span style=\"box-sizing: border-box; font-weight: bolder; color: #202124; font-family: Arial, sans-serif; font-size: 18px;\">pr&aacute;ctica profesional</span><span style=\"color: #202124; font-family: Arial, sans-serif; font-size: 18px; background-color: #ffffff;\"> al conjunto de actividades formativas de car&aacute;cter laboral y temporal que el alumno realiza con el fin de consolidar las competencias adquiridas en el aula, y que se desarrollan en el sector p&uacute;blico, social y privado.</span></p>',1,'2022-09-13 19:29:51','2022-09-20 19:57:27',NULL),(2,'¿A partir de que semestre puedo realizar mis prácticas profesionales?','2022-09-13 14:44:36','<p><span style=\"color: #757783; font-family: Arial, sans-serif; font-size: 16px; background-color: #ffffff;\">A partir del s&eacute;ptimo semestre; debe de tener cursadas todas las materias de sexto y anteriores.</span></p>',1,'2022-09-13 19:44:36','2022-09-20 19:58:33',NULL),(3,'¿Es necesario realizar prácticas profesionales en mi carrera?','2022-09-20 15:48:03','<p><span style=\"color: #757783; font-family: Arial, sans-serif; font-size: 16px; background-color: #ffffff;\">Las pr&aacute;cticas profesionales no son obligatorias, son de libre elecci&oacute;n, es decir de manera curricular que otorga cr&eacute;ditos en kardex y en caso que no encuentre deber&aacute; de llevar otras materias.</span></p>',1,'2022-09-20 20:48:03','2023-02-17 23:47:47',NULL);
