INSERT INTO `publicaciones_emprendimiento` VALUES (1,'Tool Box','tool-box','<p><span id=\"docs-internal-guid-5f2b820d-7fff-8f39-1a8f-18da59d2572a\"><span style=\"font-size: 12pt; font-family: Roboto, sans-serif; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">El Tool Box de la UANL es semanal y constituye una excelente oportunidad de conocer experiencias de los emprendedores universitarios. Cont&aacute;giate de motivaci&oacute;n y t&eacute;cnicas para llevar a cabo tus ideas.</span></span></p>','/tmp/phptME2C6',1,'2022-01-29 02:04:51','2022-07-30 03:49:59',NULL),(2,'¡FCFM en la final de Tiger Tank 2021!','fcfm-en-la-final-de-tiger-tank-2021','<p><span id=\"docs-internal-guid-bc45bf72-7fff-5b3f-414d-30276e548824\"><span style=\"font-size: 12pt; font-family: Roboto, sans-serif; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Una vez m&aacute;s la FCFM est&aacute; en la final de Tiger Tank. El a&ntilde;o pasado dos equipos estuvieron presentes y en esta edici&oacute;n no fue la excepci&oacute;n. &iexcl;Felicidades a los compa&ntilde;eros que participaron en el evento!</span></span></p>','/tmp/phpYOzg61',1,'2022-01-29 02:32:32','2022-07-30 03:50:02',NULL),(3,'Finalistas de Tiger Tank 2020','finalistas-de-tiger-tank-2020','<p><span id=\"docs-internal-guid-e2c23200-7fff-3b47-8be7-84abfa0ce8ec\"><span style=\"font-size: 12pt; font-family: Roboto, sans-serif; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">El evento de Tiger Tank cumple una vez m&aacute;s su prop&oacute;sito de incentivar la creatividad, la innovaci&oacute;n y el emprendimiento. Este a&ntilde;o 2020 uno de los finalistas es un equipo formado por cuatro elementos de los cu&aacute;les tres son de la FCFM, un orgullo para nuestra instituci&oacute;n.</span></span></p>','/tmp/phpLtmTFB',1,'2022-01-29 02:36:56','2022-07-30 03:50:05',NULL),(4,'¡Ganadores del Tiger Tank 2020!','ganadores-del-tiger-tank-2020','<p><span id=\"docs-internal-guid-287fc666-7fff-7368-b780-a916a693c073\"><span style=\"font-size: 12pt; font-family: Roboto, sans-serif; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">El equipo Xequime formado por estudiantes de diferentes facultades y entre ellos algunos de la FCFM fueron los ganadores del evento del 2020. Una enorme felicitaci&oacute;n a estos representantes de nuestra instituci&oacute;n. La mejor de las suertes en su emprendimiento y que sirvan de motivaci&oacute;n para las nuevas generaciones.</span></span></p>','/tmp/phpCj9CqQ',1,'2022-01-29 02:43:28','2022-07-30 03:50:07',NULL);
